mapper.select:
select * from (select *, ROW_NUMBER() OVER(Order by b.ID) AS RowNumber from MC_ASSOCIATE_PERSON as b where 1=1
and( b.NAME like '%${searchName}%' OR b.ADDRESS like '%${searchName}%' )
and b.ASSOCIATEID = #{associateid,jdbcType=INTEGER}
) as a where 1=1
and RowNumber BETWEEN (#{pageStart} + 1) and (#{pageSize} + #{pageStart})

select 
from MC_ASSOCIATE_PERSON
where 1=1

	and NAME=#{name}


	and ASSOCIATEID = #{associateid,jdbcType=INTEGER}

mapper.update:
update MC_ASSOCIATE_PERSON
set ASSOCIATEID = 0
where id = #{id,jdbcType=INTEGER}
