mapper.update:
update MC_ASSOCIATE_TYPE
set KEYWORD = #{keyword,jdbcType=VARCHAR},
  NAME = #{name,jdbcType=VARCHAR},
  DESCRIPTION = #{description,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select * from (select *, ROW_NUMBER() OVER(Order by b.ID DESC ) AS RowNumber from MC_ASSOCIATE_TYPE as b) as a where 1=1
and ( a.NAME like '%${searchName}%' OR a.keyword like '%${searchName}%' OR a.description like '%${searchName}%' )
and RowNumber BETWEEN #{pageStart} and #{pageSize}

	 select 

	  from MC_ASSOCIATE_TYPE 
	  where 1=1

      and  name = #{name,jdbcType=VARCHAR}


      and id = #{id,jdbcType=INTEGER}
